			margin: 2em 0 0 0;
		}

	/* Box */

		.box
		{
			padding: 25px 15px 20px 15px;
		}

			.box .image.featured
			{
				left: 15px;
				top: 15px;
				margin: -40px 0 40px -30px;
			}

	/* Button */

		input[type="button"],
		input[type="submit"],
		input[type="reset"],
		.button
		{
			position: relative;
			padding: 0.65em 0 0.65em 0 !important;
			width: 100%;
			text-align: center;
			font-size: 1.25em;
			margin: 0.25em 0 0.25em 0;
		}

