			}

				input[type="button"].alt:hover,
				input[type="submit"].alt:hover,
				input[type="reset"].alt:hover,
				.button.alt:hover
				{
					background: #353132;
				}

				input[type="button"].alt:active,
				input[type="submit"].alt:active,
				input[type="reset"].alt:active,
				.button.alt:active
				{
					background: #151112;
				}

			.button.icon
			{
			}

				.button.icon:before
				{
					opacity: 0.35;
					position: relative;
					top: 0.05em;
					margin-right: 0.75em;
				}

	/* Image */

		.image
		{
			display: inline-block;
			outline: 0;
		}

			.image img
			{
				display: block;
				width: 100%;
			}

			.image.centered
			{
				display: block;
				margin: 0 0 2em 0;
			}

				.image.centered img
				{
					margin: 0 auto;
					width: auto;
				}

			.image.featured
			{
				display: block;
				width: 100%;
				margin: 0 0 2em 0;
			}

			.image.left
			{
				float: left;
				margin: 0 2em 2em 0;
			}

	/* List */

		ul
		{
		}

			ul.default
			{
				list-style: disc;
				padding-left: 1em;
			}

				ul.default li
				{
					padding-left: 0.5em;
				}

			ul.contact
			{
			}

				ul.contact li
				{
					position: relative;
					border-top: solid 1px #ddd;
					padding: 1.3em 0 1.3em 7em;
				}

					ul.contact li h3
					{
						position: absolute;
						left: 0;
						top: 1.3em;
						font-size: 1.1em;
					}

					ul.contact li p
					{
						margin: 0;
					}

				ul.contact li:first-child
				{
					padding-top: 0;
					border-top: 0;
				}

					ul.contact li:first-child h3
					{
						top: 0;
					}

			ul.dates
			{
			}

				ul.dates li
				{
					position: relative;
					border-top: solid 1px #ddd;
					padding: 1.3em 0 1.3em 6.75em;
				}

				ul.dates .date
				{
					display: block;
					position: absolute;
					left: 0;
					top: 1.3em;
					background-color: #d52349;
					height: 3.5em;
					text-align: center;
					color: #fff;
					line-height: 1em;
					border-top-left-radius: 5px;
					border-bottom-left-radius: 5px;
					padding: 0.5em 0.75em 0 1em;
				}

					ul.dates .date:after
					{
						content: '';
						position: absolute;
						bottom: 0;
						right: -1.2em;
						border-left: solid 1.25em #d52349;
						border-top: solid 1.8em transparent;
						border-bottom: solid 1.8em transparent;
					}

					ul.dates .date strong
					{
						display: block;
						font-size: 1.75em;
						padding-top: 0.15em;
					}

				ul.dates h3
				{
					font-size: 1.1em;
				}

				ul.dates p
				{
					margin: 0;
				}

				ul.dates li:first-child
				{
					border-top: 0;
					padding-top: 0;
				}

					ul.dates li:first-child .date
					{
						top: 0;
					}

			ul.divided
			{
			}

				ul.divided li
				{
					border-top: solid 1px #ddd;
					padding: 1em 0 1em 0;
				}

				ul.divided li:first-child
				{
					border-top: 0;
					padding-top: 0;
				}

			ul.social
			{
				overflow: hidden;
				cursor: default;
			}

				ul.social li
				{
					display: inline-block;
				}

				ul.social li:first-child
				{
					margin-left: 0;
				}

				ul.social li a
				{
					display: inline-block;
					width: 3em;
					height: 3em;
					text-align: center;
					border-radius: 5px;
					background: #888;
					-moz-transition: background-color .25s ease-in-out;
					-webkit-transition: background-color .25s ease-in-out;
					-o-transition: background-color .25s ease-in-out;
					-ms-transition: background-color .25s ease-in-out;
					transition: background-color .25s ease-in-out;
				}

					ul.social li a:before
					{
						color: #fff;
						font-size: 2em;
						line-height: 1.5em;
					}

				ul.social li a.fa-facebook			{ background: #3c5a98; }
				ul.social li a.fa-facebook:hover	{ background: #4c6aa8; }
				ul.social li a.fa-twitter			{ background: #2daae4; }
				ul.social li a.fa-twitter:hover		{ background: #3dbaf4; }
				ul.social li a.fa-dribbble			{ background: #c4376b; }
				ul.social li a.fa-dribbble:hover	{ background: #d4477b; }
				ul.social li a.fa-linkedin			{ background: #006599; }
				ul.social li a.fa-linkedin:hover	{ background: #1075a9; }
				ul.social li a.fa-tumblr			{ background: #2b4661; }
				ul.social li a.fa-tumblr:hover		{ background: #3b5671; }
				ul.social li a.fa-google-plus		{ background: #da2713; }
				ul.social li a.fa-google-plus:hover	{ background: #ea3723; }

		ol
		{
		}	

			ol.default
			{
				list-style: decimal;
				padding-left: 1.25em;
			}

				ol.default li
				{
					padding-left: 0.25em;
				}

/*********************************************************************************/
/* Icons                                                                         */
/*********************************************************************************/

	.icon {
		position: relative;
		text-decoration: none;
	}

		.icon:before {
			-moz-osx-font-smoothing: grayscale;
			-webkit-font-smoothing: antialiased;
			font-family: FontAwesome;
			font-style: normal;
			font-weight: normal;
			text-transform: none !important;
		}

		.icon > .label {
			display: none;
		}

		.icon.featured
		{
			position: relative;
			display: inline-block;
			background-color: #d52349;
			width: 9em;
			padding: 1.75em 0 0.75em 0;
			border-top-left-radius: 5px;
			border-top-right-radius: 5px;
			margin: 0 0 4.5em 0;
			cursor: default;
		}

			.icon.featured:before
			{
				font-size: 4.5em;
				line-height: 1em;
				color: #fff;
			}

			.icon.featured:after
			{
				content: '';
				position: absolute;
				bottom: -1.95em;
				left: 0;
				border-top: solid 2em #d52349;
				border-left: solid 4.5em transparent;
				border-right: solid 4.5em transparent;
			}

			.icon.featured.alt
			{
				background-color: #252122;
			}

				.icon.featured.alt:after
				{
					border-top-color: #252122;
				}

			.icon.featured.alt2
			{
				background-color: #827a7c;
			}

				.icon.featured.alt2:after
				{
					border-top-color: #827a7c;
				}

/*********************************************************************************/
/* Banner                                                                        */
/*********************************************************************************/

	#banner
	{
		background: #BABE50 url('../images/pic01.jpg');
		background-position: center center;
		background-size: auto 100%;
		background-repeat: no-repeat;
		text-align: center;
	}

		#banner header
		{
			background-color: #252122;
			background-color: rgba(34,30,31,0.98);
		}

			#banner header h2
			{
				color: #fff;
				font-weight: 700;
			}

			#banner header p
			{
				color: #d52349;
				padding: 0;
				font-style: normal;
				margin: 0;
			}

/*********************************************************************************/
/* Wrappers                                                                      */
/*********************************************************************************/

	#header-wrapper
	{
		position: relative;
		background: #ffffff;
		border-bottom: solid 1px #ccc;
	}

	#main-wrapper
	{
		position: relative;
		background: #f7f7f7; /* url('images/bg02.png'); */
	}

		#main-wrapper:before
		{
			content: '';
			position: absolute;
			top: 0;
			left: 0;
			width: 100%;
			/* background: url('images/bg01.png'); */
			background-size: 100% 100%;
			z-index: 0;
			opacity: 0.1;
		}

		#main-wrapper .major
		{
		}

			#main-wrapper .major h2
			{
				/* background: #f7f7f7 url('images/bg02.png'); */
			}

	#footer-wrapper
	{
		position: relative;
		color: #858484;
	}

		#footer-wrapper:before
		{
			content: '';
			position: absolute;
			top: 0;
			left: 0;
			width: 100%;
			/* background: url('images/bg01.png'); */
			background-size: 100% 100%;
			z-index: 0;
			opacity: 0.5;
		}

		#footer-wrapper a
		{
			color: #c5c4c4;
		}

		#footer-wrapper h2,
		#footer-wrapper h3,
		#footer-wrapper h4,
		#footer-wrapper h5,
		#footer-wrapper h6,
		#footer-wrapper strong,
		#footer-wrapper b
		{
			color: #fff;
		}

		#footer-wrapper h1 a, 
		#footer-wrapper h2 a, 
		#footer-wrapper h3 a, 
		#footer-wrapper h4 a, 
		#footer-wrapper h5 a, 
		#footer-wrapper h6 a
		{
			color: inherit;
		}

		#footer-wrapper ul.contact li
		{
			border-top-color: #353434;
			border-top-color: rgba(255,255,255,0.05);
		}

		#footer-wrapper ul.dates li
		{
			border-top-color: #353434;
			border-top-color: rgba(255,255,255,0.05);
		}

		#footer-wrapper ul.divided li
		{
			border-top-color: #353434;
			border-top-color: rgba(255,255,255,0.05);
		}

/*********************************************************************************/
/* Intro                                                                         */
/*********************************************************************************/

	#intro
	{
		text-align: center;
	}

/*********************************************************************************/
/* Copyright                                                                     */
/*********************************************************************************/

	#copyright
	{	
		text-align: center;
	}

		#copyright .links
		{
			display: inline-block;
			background: #2C2929;
			background: rgba(255,255,255,0.05);
			border-radius: 5px;
			margin: 0;
		}
/*********************************************************************************/
/* Added                                                                     */
/*********************************************************************************/

